import React, {FC} from 'react';
import styles from './app.module.css'
import AppHeader from "../appHeader/appHeader";
import BurgerIngredients from "../burgerIngredients/burgerIngredients";
import BurgerConstructor from "../burgerConstructor/burgerConstructor";


interface State {
    _id: string
    name: string
    type: string
    proteins: number
    fat: number
    carbohydrates: number
    calories: number
    price: number
    image: string
    image_mobile: string
    image_large: string
    __v: number


}

interface PromiseMy {
    success: boolean
    data: any;
}


async function getState(url: string, setter: Function): Promise<PromiseMy> {
    const response = await fetch(url, {method: 'GET'})

    return response.json()
}

const App: FC = () => {

    //получение данных
    const url: string = 'https://norma.nomoreparties.space/api/ingredients'
    const [promiseMy, setPromise] = React.useState<PromiseMy | null>(null)
    const [state, setState] = React.useState<State[] | null>(null)


    const [stateChoice, setStateChoice] = React.useState<State[] | null>(null)

    //получаем промисы
    React.useEffect(
        () => {

            getState(url, setState).then(data => setPromise(data))

        }, []
    )

    //при получении промисов записываем данные в state
    React.useEffect(
        () => {

            setState(promiseMy?.data)

        }, [promiseMy]
    )

    const getIdIngredients = (e: string) => {
        let newIng : State
        newIng = state.filter(el => el._id === e.currentTarget.id)
        setChoiceModalIng([...newIng])
        onModalIngActive()
        onModalActive()
    }


    return (
        <div className={styles.page}>

            <div className={styles.app_wrapper}>
                <AppHeader/>
                <BurgerIngredients state={state}/>
                <BurgerConstructor stateChoice={stateChoice}/>
            </div>

        </div>
    );
}

export default App;
