import React, {FC} from 'react';
import styles from './app.module.css'


interface State {
    _id: string
    name: string
    type: string
    proteins: number
    fat: number
    carbohydrates: number
    calories: number
    price: number
    image: string
    image_mobile: string
    image_large: string
    __v: number


}

interface PromiseMy {
    success: boolean
    data: any;
}


async function getState(url: string, setter: Function): Promise<PromiseMy> {
    const response = await fetch(url, {method: 'GET'})

    return response.json()
}

const App: FC = () => {

    const url: string = 'https://norma.nomoreparties.space/api/ingredients'

    const [promiseMy, setPromise] = React.useState<PromiseMy | null>(null)
    const [state, setState] = React.useState<State[] | null>(null)

    React.useEffect(
        () => {

            getState(url, setState).then(data => setPromise(data))


        }, []
    )

    React.useEffect(
        () => {

            setState(promiseMy?.data)

        }, [promiseMy]
    )


    return (
        <div className={styles.page}>


        </div>
    );
}

export default App;
