import React from "react";
import styles from './modalOverlay.module.css'
import Modal from "./modal";

interface Props {
    props?: React.ReactNode
    active: Function
}

const ModalOverlay: React.FC<Props> = ({
                                           active

                                       }) => {


    return (
        <div className={active ? (styles.modal + ' ' + styles.active) : styles.modal}
             onClick={active(false)}>
            <Modal setModalOrderActive={setModalOrderActive} active={props.active}
                   modalOrderActive={modalOrderActive} choiceModalIng={props.choiceModalIng}
                   setModalActive={setModalActive} activeIng={props.activeIng}
                   setModalIngActive={setModalIngActive}/>
        </div>
    )
}


export default ModalOverlay
