import React, {FC} from 'react';
import axios from "axios";

interface State {
    _id: string
    name: string
    type: string
    proteins: number
    fat: number
    carbohydrates: number
    calories: number
    price: number
    image: string
    image_mobile: string
    image_large: string
    __v: number


}

//
// const getState = (url: string, setter: Function) => {
//     axios.get(url).then(response => {
//             setter(response.data.data)
//         }
//     )
// }

async function getState(url: string, setter: Function): Promise<State> {
    const response = await fetch(url, {method: 'GET'})

    return response.json()
}

const App: FC = () => {

    const url: string = 'https://norma.nomoreparties.space/api/ingredients'

    const [state, setState] = React.useState<State | null>(null)

    // @ts-ignore
    React.useEffect(
        () => {
            getState(url, setState).then(data => setState(data))

        }, []
    )

    const e = () => {
        console.log(state.data)
    }


    return (
        <div className="App">
            <button onClick={e}/>
            {state?._id}
        </div>
    );
}

export default App;
