import React from 'react';
import {
    CurrencyIcon,
} from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burgerIngrSub.module.css'


interface State {
    _id: string
    name: string
    type: string
    proteins: number
    fat: number
    carbohydrates: number
    calories: number
    price: number
    image: string
    image_mobile: string
    image_large: string
    __v: number


}


interface Props {
    props?: React.ReactNode
    state: any


}

const BurgerIngSub: React.FC<Props> = ({
                                           state
                                       }) => {

    // const handelStateChoice = (e) => {
    //     props.onChange(e.currentTarget.id)
    // }
    //
    // const getIdIngredients = (e) => {
    //     props.getIdIngredients(e)
    // }

    const component: State[] = state.map((el: State) => (
        <div key={el._id} className={styles.component}>
            <div className={styles.componentImage}>
                <button id={el._id} className={styles.currencyIconButton}><img src={el.image}
                                                                               alt={''}/>
                </button>
            </div>
            <div className={styles.componentPrice}>{el.price} <CurrencyIcon type={'primary'}/></div>
            <button id={el._id} className={styles.componentText}>{el.name}</button>


        </div>

    ))

    return (
        <div className={styles.componentMain}>
            {component}
        </div>
    )
}


export default BurgerIngSub
